mapper.select:
SELECT lesson
FROM score
WHERE class_id = #{class_id}
GROUP BY lesson
ORDER BY lesson ASC;

SELECT *
FROM score
WHERE class_id = #{class_id} and student_id = #{student_id} and lesson = #{lesson}

	SELECT count(*) > 0 
    FROM score 
    WHERE student_id = #{student_id} AND lesson = #{lesson}

mapper.update:
UPDATE score
SET score = #{score}, error = #{error}, comment = #{comment}
WHERE student_id = #{student_id} AND lesson = #{lesson};
